Public Sub IccDateTimeTagTypeExample() ' load an Icc Profile Dim iccProfile As New IccProfileExtended(Path.Combine(LEAD_VARS.ImagesDir, "EmptyIcc.icc"))
' get the current system data Dim sysDateTime As DateTime = DateTime.Now
' create a new iccDateTime class Dim iccDateTime As New IccDateTime(CUShort(sysDateTime.Year), _ CUShort(sysDateTime.Month), _ CUShort(sysDateTime.Day), _ CUShort(sysDateTime.Hour), _ CUShort(sysDateTime.Minute), _ CUShort(sysDateTime.Second))
' define the tag type Dim iccDateTimeTagType As New IccDateTimeTagType(iccDateTime)
' add the new tag to the ICC Profile iccProfile.AddTag(iccDateTimeTagType, IccTag.CalibrationDateTimeTag, IccTagTypeBase.DateTimeTypeSignature)
' generate the new profile id iccProfile.GenerateProfileId()
' update the icc array with the new changes iccProfile.UpdateDataArray()
