   Contains the chromaticityType tag type data. 




         The signature for this tag type is .




           Public Sub IccChromaticityTagTypeExample()
      ' load an Icc Profile
      Dim iccProfile As New IccProfileExtended(Path.Combine(LEAD_VARS.ImagesDir, "EmptyIcc.icc"))

      ' define the three channels
      Dim iccChan(2) As IccChannelCoordinates

      ' define the first channel coordinates
      Dim coord(1) As Integer
      coord(0) = IccTools.FromDoubleTo2bFixed2bNumber(0.64)
      coord(1) = IccTools.FromDoubleTo2bFixed2bNumber(0.33)
      iccChan(0) = New IccChannelCoordinates(coord)

      ' define the second channel coordinates
      coord(0) = IccTools.FromDoubleTo2bFixed2bNumber(0.3)
      coord(1) = IccTools.FromDoubleTo2bFixed2bNumber(0.6)
      iccChan(1) = New IccChannelCoordinates(coord)

      ' define the third channel coordinates
      coord(0) = IccTools.FromDoubleTo2bFixed2bNumber(0.15)
      coord(1) = IccTools.FromDoubleTo2bFixed2bNumber(0.06)
      iccChan(2) = New IccChannelCoordinates(coord)

      ' create the IccChomaticity class
      Dim iccChrom As New IccChromaticity(IccColorantsType.ITU_R_BT709, iccChan)

      ' define the tag type
      Dim iccChromTagType As New IccChromaticityTagType(iccChrom)

      ' add the new tag to the ICC Profile
      iccProfile.AddTag(iccChromTagType, IccTag.ChromaticityTag, IccTagTypeBase.ChromaticityTypeSignature)

      ' generate the new profile id
      iccProfile.GenerateProfileId()

      ' update the icc array with the new changes
      iccProfile.UpdateDataArray()

      ' write the Icc Profile into a new file
      iccProfile.GenerateIccFile(Path.Combine(LEAD_VARS.ImagesDir, "IccChromaticityTagTypeVB.icc"))
   End Sub

