           Public Sub IccTextTagTypeExample()
      ' load an Icc Profile
      Dim iccProfile As New IccProfileExtended(Path.Combine(LEAD_VARS.ImagesDir, "EmptyIcc.icc"))

      ' define the text data array, the string must be null terminated
      Dim data() As Byte = {CByte(AscW("a"c)), CByte(AscW("b"c)), CByte(AscW("c"c)), 0}

      ' define the tag type
      Dim textTagType As New IccTextTagType(data)

      ' add the new tag to the ICC Profile
      iccProfile.AddTag(textTagType, IccTag.CharTargetTag, IccTagTypeBase.TextTypeSignature)

      ' generate the new profile id
      iccProfile.GenerateProfileId()

      ' update the icc array with the new changes
      iccProfile.UpdateDataArray()
      iccProfile.UpdateDataArray()

      ' write the Icc Profile into a new file
      iccProfile.GenerateIccFile(Path.Combine(LEAD_VARS.ImagesDir, "IccTextTagTypeVB.icc"))
   End Sub

Public NotInheritable Class LEAD_VARS
   Public Const ImagesDir As String = "C:\Users\Public\Documents\LEADTOOLS Images"
End Class
           public void IccTextTagTypeExample()
   {
      // load an Icc Profile
      string fileName = Path.Combine(LEAD_VARS.ImagesDir, "EmptyIcc.icc");
      IccProfileExtended iccProfile = new IccProfileExtended(fileName);

      // define the text data array, the string must be null terminated
      byte[] data = new byte[4] { (byte)'a', (byte)'b', (byte)'c', (byte)'\0' };

      // define the tag type
      IccTextTagType textTagType = new IccTextTagType(data);

      // add the new tag to the ICC Profile
      iccProfile.AddTag(textTagType, IccTag.CharTargetTag, IccTagTypeBase.TextTypeSignature);

      // generate the new profile id
      iccProfile.GenerateProfileId();

      // update the icc array with the new changes
