           Public Sub IccUnknownTagTypeExample()
      ' load an Icc Profile
      Dim iccProfile As New IccProfileExtended(Path.Combine(LEAD_VARS.ImagesDir, "EmptyIcc.icc"))

      ' define the unknown tag type signature
      Dim unknownSignature As Integer = &H736C6364

      ' define the unknown tag data
      Dim data() As Byte = {1, 2, 3}

      ' define the tag type
      Dim unknownTagType As New IccUnknownTagType(unknownSignature, data)

      ' add the new tag to the ICC Profile
      iccProfile.AddTag(unknownTagType, &H54616746, unknownSignature)

      ' generate the new profile id
      iccProfile.GenerateProfileId()

      ' update the icc array with the new changes
      iccProfile.UpdateDataArray()

      ' write the Icc Profile into a new file
      iccProfile.GenerateIccFile(Path.Combine(LEAD_VARS.ImagesDir, "IccUnknownTagTypeVB.icc"))
