               labBuffer, // output buffer
               0, // offset from the beginning of the destination buffer
               bgrImage.Width, // pixels width
               bgrImage.Height, // pixels height
               bgrImage.BytesPerLine - (bgrImage.Width * (bgrImage.BitsPerPixel / 8)),
               0); // 0 bytes align

